<?php namespace Home\Controller;class IndexController extends HomeController{        public function index(){           	$d=D("Info");        	//焦点图        	$this->focus = D("Focus")->where(array("tid"=>5))->order("sort desc")->limit(5)->select();//                var_dump($this->focus);die;        	//涉及领域        	$this->field = $d->where(array("tid"=>12))->order("sort asc,time desc")->limit(3)->select();        	//我们的案例        	$this->case  = $d->where(array("tid"=>13))->order("sort asc,time desc")->limit(4)->select();            //合作伙伴            $this->friends=$d->where(array("tid"=>18))->order('sort asc')->limit(12)->select();            $this->display();        }                        public function index_back(){           	$d=D("Info");        	//焦点图        	$this->focus = D("Focus")->where(array("tid"=>5))->order("sort desc")->limit(5)->select();             	//涉及领域        	$this->field = $d->where(array("tid"=>12))->order("sort asc,time desc")->limit(3)->select();        	//我们的案例        	$this->case  = $d->where(array("tid"=>13))->order("sort asc,time desc")->limit(4)->select();            //合作伙伴            $this->friends=$d->where(array("tid"=>18))->order('sort asc')->limit(12)->select();            $this->display();        }        }?>