<?php namespace Mobile\Controller;class IndexController extends MobileController{        public function index(){        	$d=D("Info");        	//焦点图        	$this->focus = D("Focus")->where(array("tid"=>37))->order("sort desc")->limit(3)->select();        	//产品服务        	//$this->product = D("Type")->where(array("pid"=>38))->field('id,title')->order("sort asc")->limit(6)->select();        	//应用案例(智能机器人)        	$this->robot  = $d->where(array("tid"=>48))->order("sort asc,time desc")->select();            //应用案例(VR)            $this->vr  = $d->where(array("tid"=>49))->order("sort asc,time desc")->select();            //新闻            $this->news = $d->where(array("tid"=>array('in','9,10')))->limit(3)->order('time desc')->select();            $this->display();        }}?>